import { Component } from '@angular/core';

@Component({
  selector: 'app-header',
  templateUrl: './header.component.html',
  styleUrls: ['./header.component.scss']
})
export class HeaderComponent {
  appName = '家用智慧助理系統';
}
<header class="header">
  <div class="logo">
    <h1>{{ appName }}</h1>
  </div>
  <nav class="nav-links">
    <ul>
      <li><a routerLink="/dashboard" routerLinkActive="active">儀表板</a></li>
      <li><a routerLink="/schedule" routerLinkActive="active">行事曆</a></li>
      <li><a routerLink="/consumable" routerLinkActive="active">耗材管理</a></li>
    </ul>
  </nav>
</header>
