import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { Consumable, ConsumableCreateDto } from '../../shared/models/consumable.model';
import { ConsumableService } from '../../shared/services/consumable.service';

@Component({
  selector: 'app-consumable',
  templateUrl: './consumable.component.html',
  styleUrls: ['./consumable.component.scss']
})
export class ConsumableComponent implements OnInit {
  consumables: Consumable[] = [];
  consumableForm: FormGroup;
  isLoading = true;
  isSubmitting = false;
  editingConsumableId: number | null = null;
  showForm = false;
  error: string | null = null;
  
  // 耗材分類選項
  categories = [
    '濾水器',
    '空氣清淨機濾網',
    '冷氣濾網',
    '吸塵器濾網',
    '其他'
  ];

  constructor(
    private consumableService: ConsumableService,
    private fb: FormBuilder
  ) {
    this.consumableForm = this.fb.group({
      name: ['', [Validators.required]],
      category: ['', [Validators.required]],
      installation_date: ['', [Validators.required]],
      lifetime_days: [90, [Validators.required, Validators.min(1)]],
      notes: ['']
    });
  }

  ngOnInit(): void {
    this.loadConsumables();
  }

  loadConsumables(): void {
    this.isLoading = true;
    this.consumableService.getConsumables().subscribe({
      next: (data) => {
        this.consumables = data;
        this.isLoading = false;
      },
      error: (error) => {
        console.error('Error loading consumables', error);
        this.error = '無法載入耗材資料';
        this.isLoading = false;
      }
    });
  }

  onSubmit(): void {
    if (this.consumableForm.invalid) {
      return;
    }

    this.isSubmitting = true;
    const formData = this.consumableForm.value;

    // Format dates properly for API
    const consumableData: ConsumableCreateDto = {
      name: formData.name,
      category: formData.category,
      installation_date: formData.installation_date,
      lifetime_days: formData.lifetime_days,
      notes: formData.notes
    };

    if (this.editingConsumableId) {
      this.consumableService.updateConsumable(this.editingConsumableId, consumableData).subscribe({
        next: () => {
          this.resetForm();
          this.loadConsumables();
        },
        error: (error) => {
          console.error('Error updating consumable', error);
          this.error = '更新耗材失敗';
          this.isSubmitting = false;
        }
      });
    } else {
      this.consumableService.createConsumable(consumableData).subscribe({
        next: () => {
          this.resetForm();
          this.loadConsumables();
        },
        error: (error) => {
          console.error('Error creating consumable', error);
          this.error = '新增耗材失敗';
          this.isSubmitting = false;
        }
      });
    }
  }

  editConsumable(consumable: Consumable): void {
    if (consumable.id) {
      this.editingConsumableId = consumable.id;
      this.showForm = true;
      
      this.consumableForm.patchValue({
        name: consumable.name,
        category: consumable.category,
        installation_date: this.formatDateForInput(new Date(consumable.installation_date)),
        lifetime_days: consumable.lifetime_days,
        notes: consumable.notes
      });
    }
  }

  deleteConsumable(id: number): void {
    if (confirm('確定要刪除此耗材記錄嗎?')) {
      this.consumableService.deleteConsumable(id).subscribe({
        next: () => {
          this.loadConsumables();
        },
        error: (error) => {
          console.error('Error deleting consumable', error);
          this.error = '刪除耗材失敗';
        }
      });
    }
  }

  resetForm(): void {
    this.consumableForm.reset({
      category: '',
      lifetime_days: 90
    });
    this.editingConsumableId = null;
    this.isSubmitting = false;
    this.showForm = false;
  }

  toggleForm(): void {
    if (this.showForm) {
      this.resetForm();
    } else {
      this.showForm = true;
    }
  }

  getStatusClass(daysRemaining?: number): string {
    if (daysRemaining === undefined) return '';
    if (daysRemaining <= 0) return 'badge-danger';
    if (daysRemaining < 30) return 'badge-warning';
    return 'badge-success';
  }

  // Helper to format date for date input (YYYY-MM-DD)
  private formatDateForInput(date: Date): string {
    return date.toISOString().split('T')[0];
  }
}
<div class="container">
  <div class="page-header">
    <h1 class="page-title">耗材管理</h1>
    <button class="btn" (click)="toggleForm()">
      {{ showForm ? '取消' : '新增耗材' }}
    </button>
  </div>

  <div *ngIf="error" class="alert alert-danger">{{ error }}</div>

  <!-- 新增/編輯表單 -->
  <div class="card" *ngIf="showForm">
    <h2>{{ editingConsumableId ? '編輯耗材' : '新增耗材' }}</h2>
    
    <form [formGroup]="consumableForm" (ngSubmit)="onSubmit()">
      <div class="form-group">
        <label for="name">名稱</label>
        <input type="text" id="name" formControlName="name" class="form-control" placeholder="請輸入耗材名稱">
        <div *ngIf="consumableForm.get('name')?.invalid && consumableForm.get('name')?.touched" class="error-message">
          名稱為必填項目
        </div>
      </div>
      
      <div class="form-group">
        <label for="category">分類</label>
        <select id="category" formControlName="category" class="form-control">
          <option value="">選擇分類</option>
          <option *ngFor="let category of categories" [value]="category">{{ category }}</option>
        </select>
        <div *ngIf="consumableForm.get('category')?.invalid && consumableForm.get('category')?.touched" class="error-message">
          分類為必填項目
        </div>
      </div>
      
      <div class="form-group">
        <label for="installation_date">安裝日期</label>
        <input type="date" id="installation_date" formControlName="installation_date" class="form-control">
        <div *ngIf="consumableForm.get('installation_date')?.invalid && consumableForm.get('installation_date')?.touched" class="error-message">
          安裝日期為必填項目
        </div>
      </div>
      
      <div class="form-group">
        <label for="lifetime_days">使用壽命(天)</label>
        <input type="number" id="lifetime_days" formControlName="lifetime_days" class="form-control" min="1">
        <div *ngIf="consumableForm.get('lifetime_days')?.invalid && consumableForm.get('lifetime_days')?.touched" class="error-message">
          使用壽命必須大於0
        </div>
      </div>
      
      <div class="form-group">
        <label for="notes">備註</label>
        <textarea id="notes" formControlName="notes" class="form-control" rows="3" placeholder="請輸入備註"></textarea>
      </div>
      
      <div class="form-actions">
        <button type="submit" class="btn" [disabled]="consumableForm.invalid || isSubmitting">
          {{ isSubmitting ? '處理中...' : (editingConsumableId ? '更新' : '新增') }}
        </button>
        <button type="button" class="btn btn-secondary" (click)="resetForm()">取消</button>
      </div>
    </form>
  </div>

  <!-- 耗材列表 -->
  <div class="card">
    <div class="card-header">
      <h2>所有耗材</h2>
      <button class="btn btn-secondary" (click)="loadConsumables()">重新載入</button>
    </div>
    
    <app-loading-spinner *ngIf="isLoading"></app-loading-spinner>
    
    <div *ngIf="!isLoading">
      <div *ngIf="consumables.length === 0" class="empty-state">
        目前沒有耗材記錄，請點擊"新增耗材"按鈕來新增。
      </div>
      
      <div class="consumables-grid">
        <div *ngFor="let consumable of consumables" class="consumable-card">
          <div class="consumable-header">
            <h3>{{ consumable.name }}</h3>
            <span class="badge category-badge">{{ consumable.category }}</span>
          </div>
          
          <div class="consumable-details">
            <div class="detail-row">
              <span class="detail-label">安裝日期:</span>
              <span class="detail-value">{{ consumable.installation_date | date:'yyyy-MM-dd' }}</span>
            </div>
            
            <div class="detail-row">
              <span class="detail-label">使用壽命:</span>
              <span class="detail-value">{{ consumable.lifetime_days }} 天</span>
            </div>
            
            <div class="detail-row">
              <span class="detail-label">狀態:</span>
              <span class="detail-value">
                <span class="badge" [ngClass]="getStatusClass(consumable.days_remaining)">
                  <span *ngIf="consumable.days_remaining !== undefined && consumable.days_remaining > 0">
                    剩餘: {{ consumable.days_remaining }} 天
                  </span>
                  <span *ngIf="consumable.days_remaining !== undefined && consumable.days_remaining <= 0">
                    已過期
                  </span>
                </span>
              </span>
            </div>
            
            <div class="detail-notes" *ngIf="consumable.notes">
              <p>{{ consumable.notes }}</p>
            </div>
          </div>
          
          <div class="consumable-actions">
            <button class="btn-icon edit" (click)="editConsumable(consumable)" title="編輯">✏️</button>
            <button class="btn-icon delete" (click)="deleteConsumable(consumable.id!)" title="刪除">🗑️</button>
          </div>
        </div>
      </div>
    </div>
  </div>
</div>
